// frontend/src/App.jsx - Main React component for Bug Whisperer
import React, { useState } from 'react';
import './App.css';

function App() {
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [filename, setFilename] = useState('example.js');
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleAnalyze = async () => {
    if (!code.trim()) {
      setError('Please enter some code to analyze');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          language,
          filename,
          code
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Analysis failed');
      }

      const result = await response.json();
      setAnalysis(result);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const getSeverityColor = (severity) => {
    return severity === 'error' ? '#dc3545' : '#ffc107';
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>üêõ Bug Whisperer</h1>
        <p>JavaScript Code Analyzer with ESLint</p>
      </header>

      <main className="App-main">
        <div className="input-section">
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="language">Language:</label>
              <select 
                id="language"
                value={language} 
                onChange={(e) => setLanguage(e.target.value)}
              >
                <option value="javascript">JavaScript</option>
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="filename">Filename:</label>
              <input
                id="filename"
                type="text"
                value={filename}
                onChange={(e) => setFilename(e.target.value)}
                placeholder="example.js"
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="code">Code to analyze:</label>
            <textarea
              id="code"
              value={code}
              onChange={(e) => setCode(e.target.value)}
              placeholder="Paste your JavaScript code here..."
              rows={12}
            />
          </div>

          <button 
            onClick={handleAnalyze} 
            disabled={loading}
            className="analyze-btn"
          >
            {loading ? 'Analyzing...' : 'Analyze Code'}
          </button>
        </div>

        {error && (
          <div className="error-message">
            <strong>Error:</strong> {error}
          </div>
        )}

        {analysis && (
          <div className="results-section">
            <div className="stats">
              <h3>Analysis Results</h3>
              <div className="stats-grid">
                <div className="stat">
                  <span className="stat-number">{analysis.stats.total_issues}</span>
                  <span className="stat-label">Total Issues</span>
                </div>
                <div className="stat error">
                  <span className="stat-number">{analysis.stats.errors}</span>
                  <span className="stat-label">Errors</span>
                </div>
                <div className="stat warning">
                  <span className="stat-number">{analysis.stats.warnings}</span>
                  <span className="stat-label">Warnings</span>
                </div>
                <div className="stat success">
                  <span className="stat-number">{analysis.stats.auto_fixed ? 'Yes' : 'No'}</span>
                  <span className="stat-label">Auto-Fixed</span>
                </div>
              </div>
            </div>

            {analysis.issues.length > 0 && (
              <div className="issues-section">
                <h4>Issues Found</h4>
                <div className="issues-table">
                  <table>
                    <thead>
                      <tr>
                        <th>Line</th>
                        <th>Severity</th>
                        <th>Message</th>
                        <th>Suggestion</th>
                      </tr>
                    </thead>
                    <tbody>
                      {analysis.issues.map((issue, index) => (
                        <tr key={index}>
                          <td>{issue.line}</td>
                          <td>
                            <span 
                              className="severity-badge"
                              style={{ backgroundColor: getSeverityColor(issue.severity) }}
                            >
                              {issue.severity}
                            </span>
                          </td>
                          <td>{issue.message}</td>
                          <td className="suggestion">{issue.suggestion}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}

            <div className="fixed-code-section">
              <h4>Fixed Code</h4>
              <pre className="code-block">
                <code>{analysis.fixed_code}</code>
              </pre>
            </div>

            <div className="lesson-section">
              <h4>üí° Learning Lesson</h4>
              <p className="lesson-text">{analysis.lesson}</p>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
